struct Server{
    
    char distCenter[50];
    int port;
    char host[128];
    int time;
    struct Server *next;
};

typedef struct Server bestServer;

struct List {

    bestServer *begin;
    bestServer *end;
    int size;
};

typedef struct List list;

void inicializeList(list *target){

    (*target).begin = NULL;
    (*target).end = NULL;
    (*target).size = 0;
}

void addList(list *target, bestServer *object){

    bestServer *temp = (*target).begin;
    bestServer *temp2;

    int n = 0;

    if ((*target).size == 0){
        (*target).begin = object;
        (*target).end = object;
        (*object).next = NULL;
        (*target).size = 1;
        n = 1;
        temp = NULL;
    } else {

        while(temp2 != NULL){
            if ((*temp2).time >= (*object).time){
                (*object).next = (*temp2).next;
                (*temp).next = object;
                n = 1;
                break;
            }
            temp = temp2;
            temp2 = (*temp2).next;
        }
    }

    if (n == 0){
        (*target).end = object;
        (*object).next = NULL;
    }

    (*target).size += 1;
}

